"use strict";
//Comparision Operator
//is m ham value ys data ko compare kartay hn ky ye barabar hy ya bari hy ya choti hy ya kuch bhi.
// ab ya to ham vakue compare karwayn gy ya data type ya or bhi kuch ho sakta hy value compare karwanay ky liye ham == istmaal karty h  or data type ko bhi campare karwana hy to === istmaal karty hn.
let MyFacebookPassword = 'aliarain12345';
let MyNewFacebookPassword = 'AliArain12345';
console.log('is m ham fb ki value check kar rahay hy', MyFacebookPassword == MyNewFacebookPassword);
let MyinstaPassword = '12345';
let MyNewinstaPassword = 12345;
console.log('is m ham insta ki value check kar rahay hn', MyinstaPassword == MyNewinstaPassword);
console.log('insta ki value or type bhi check kar rahay hn', MyinstaPassword === MyNewinstaPassword);
console.log('bera garak', MyinstaPassword != MyNewinstaPassword);
console.log('bera garak', MyinstaPassword !== MyNewinstaPassword);
