"use strict";
let burger = "My";
console.log(burger);
// burger =20 in the variable type string we can't put the value in number
// giving function will not changr the actual value in premitives data types
console.log('BURGER', burger.toUpperCase());
// actual value remain same 
console.log('actual value:', burger);
let myAge = 17.88990;
console.log(myAge);
//giving function will not changr the actual value in premitives data types
console.log('my age', myAge.toFixed(1));
// actual value remain same 
console.log('actual value:', myAge);
let mycar = false;
console.log(mycar);
// giving function will not changr the actual value in premitives data types
console.log('my car', mycar.valueOf()); // because this function gives actual value this is why we'll not print him again
